export const userSettings=window.localStorage;const theme=window.matchMedia("(prefers-color-scheme: light)").matches?"light":"dark";class UserSettings{constructor(){userSettings.getItem("theme")||userSettings.setItem("theme",theme)}async updateTheme(){(await import("/retail-codes/assets/scripts/theme-updater.min.mjs")).default(!0)}}function getInfo(e){return userSettings.getItem(e)}export default UserSettings=new UserSettings;